'DECLARE',
'    v_filas_insertadas NUMBER := 0;',
'    v_count_parseo NUMBER := 0;',
'    v_count NUMBER := 0;',
'    v_error_count NUMBER := 0;',
'    v_fila NUMBER :=0;',
'BEGIN',
unistr('    -- Informaci\00F3n de depuraci\00F3n'),
'    apex_debug.info(''Mod: Equipos: Func: DL: File Name: '' || :P28_FILE_NAME);',
'    apex_debug.info(''Mod: Equipos: Func: DL: Archivo: ''   || :P28_XLSX_WORKSHEET);',
'',
'    -- Bucle por cada fila del archivo',
'    -- Seleccionar los campos que son importantes y que no pueden ser nulos',
'    FOR p IN (',
'        SELECT *',
'        FROM apex_application_temp_files f,',
'             TABLE(apex_data_parser.parse(',
'                 p_content   => f.blob_content,',
'                 p_file_name => f.filename,',
'                 p_skip_rows => 1',
'             )) tab',
'        WHERE f.name = :P6_FILE',
'          AND col002 IS NOT NULL ',
'          AND col006 is not NULL',
'    ) LOOP',
'',
'        BEGIN',
'            -- Intentar insertar cada fila',
'            INSERT INTO SGT_EQUIPOS (',
'                UNIDAD_ENCARGADA,',
'                RACK_ID,',
'                U,',
'                TIPO_RED,',
'                NOMBRE,',
'                TIPO_EQUIPO_ID,',
'                SUB_TIPO_EQUIPO,',
'',
'                MARCA,',
'                MODELO,',
'                ESTADO,',
'                OBSERVACIONES,',
'                NI_ANDE,',
'                SERIAL_NUMBER,',
'                VERSION_HARDWARE,',
'',
'                VERSION_SOFTWARE,',
'                VERSION_PATCH,',
'                IP_GESTION,',
'                ALIMENTACION1_ID,',
'                ALIMENTACION2_ID,',
'                PROYECTO_ID,',
'                FECHA_IMPLE,',
'',
'                ALTURA_U,',
'                POSICION, ',
'                SITIO_ID',
'                ',
'',
'',
'            ) VALUES (',
'                p.col001, --UNIDAD',
'                F_GET_LOVS(3, p.col002), --RACK',
'                p.col003, --u',
'                p.col004, --tipo RED',
'                upper(p.col005), --NOMBRE',
'                F_GET_LOVS(5, p.col006), --tIPO eQUIPO',
'                LOWER(p.col007), --sub_tipo_equipo',
'',
'                upper(p.col008), --marca',
'                upper(p.col009), --modelo',
'                LOWER(p.col010),  --estado',
'                p.col011, --observaciones',
'                p.col012, --ni_ande',
'                p.col013, --serial_number',
'                p.col014, --version_hard',
'',
'                p.col015, --version_soft',
'                p.col016, --patch_ver',
'                p.col017, --ip_gestion',
'                p.col018, --ali1',
'                p.col019, --ali2',
'                p.col020, --pro_id',
'                parse_flexible_date(p.col021), --fecha_imple',
'',
'                (p.col022), -- altura',
'                UPPER(p.col023), --pos,',
'                F_GET_LOVS(4, p.col002) --SITIO  ',
'            );',
'',
'            v_count := v_count + 1;',
'',
'        EXCEPTION',
'            WHEN DUP_VAL_ON_INDEX THEN',
'                v_error_count := v_error_count + 1;',
'                apex_debug.error(''Mod: Equipos: Func: DL: Fila duplicada: '' || SQLERRM);',
'                CONTINUE;',
'',
'            WHEN OTHERS THEN',
'                v_error_count := v_error_count + 1;',
'                apex_debug.error(''Mod: Equipos: Func: DL: Error en fila '' || TO_CHAR(v_count + v_error_count) || '': '' || SQLERRM);',
'                CONTINUE;',
'        END;',
'',
'    END LOOP;',
'',
'    :P6_ERROR_ROW_COUNT := v_error_count;',
'    :P6_LOGGER := ''Registros insertados: '' || v_count || '', Errores: '' || v_error_count;',
'',
'    apex_debug.info(''Registros insertados: '' || v_count);',
'    apex_debug.info(''Errores: '' || v_error_count);',
'',
'   ',
'END;',
''))
