'            -- Intentar insertar cada fila',
'            INSERT INTO SGT_INTERFACES (',
'                EQUIPO_ID,',
'                NOMBRE,',
'                ETIQUETA,',
'                ESTADO,',
'                MODO,',
'                DUPLEX,',
