'/',
'create or replace PACKAGE BODY AUDIT_PKG AS  ',
'    FUNCTION START_AUDIT (  ',
'        p_table_name  IN VARCHAR2,  ',
'        p_action      IN VARCHAR2  ',
'    ) RETURN NUMBER IS  ',
'        v_audit_id NUMBER;  ',
