'    sitio_b.siglas, ',
'    sitio_b.id, ',
'    eq_b.nombre AS equipo_b, ',
'    inter_b.nombre AS interfaz_b, ',
'    et.tipo_conexion as tipo_conexion, ',
'    et.enlace_id as enlace_id ',
'FROM  ',
'    sgt_enlaces_tramos et ',
' ',
'    INNER JOIN sgt_equipos eq_a ON et.equipo_a_id = eq_a'))
);
