'DECLARE',
'    v_filas_insertadas NUMBER := 0;',
'    v_count_parseo NUMBER := 0;',
'    v_count NUMBER := 0;',
'    v_error_count NUMBER := 0;',
'    v_fila NUMBER :=0;',
'BEGIN',
unistr('    -- Informaci\00F3n de depuraci\00F3n'),
'    apex_debug.info(''Mod: Equipos: Func: DL: File Name: '' || :P6_FILE_NAME);',
'    apex_debug.info(''Mod: Equipos: Func: DL: Archivo: ''   || :P6_XLSX_WORKSHEET);',
'',
'    -- Bucle por cada fila del archivo',
'    -- Seleccionar los campos que son importantes y que no pueden ser nulos',
'    FOR p IN (',
'        SELECT *',
'        FROM apex_application_temp_files f,',
'             TABLE(apex_data_parser.parse(',
'                 p_content   => f.blob_content,',
'                 p_file_name => f.filename,',
'                 p_xlsx_sheet_name => NVL(:P6_XLSX_WORKSHEET, NULL),',
'                 p_skip_rows => 1',
'             )) tab',
'        WHERE f.name = :P6_FILE',
'          AND col002 IS NOT NULL ',
'          AND col006 is not NULL',
'    ) LOOP',
'',
'        BEGIN',
'            -- Intentar insertar cada fila',
'            INSERT INTO SGT_EQUIPOS (',
'                UNIDAD_ENCARGADA,',
'                RACK_ID,',
'                U,',
'                TIPO_RED,',
'                NOMBRE,',
'                TIPO_EQUIPO_ID,',
'                --SUB_TIPO_EQUIPO,',
'',
'                MARCA,',
'                MODELO,',
'                ESTADO,',
'                OBSERVACIONES,',
'                NI_ANDE,',
'                SERIAL_NUMBER,',
'                VERSION_HARDWARE,',
'',
'                VERSION_SOFTWARE,',
'                VERSION_PATCH,',
'                IP_GESTION,',
'                ALIMENTACION1_ID,',
'                ALIMENTACION2_ID,',
'                PROYECTO_ID,',
'                FECHA_IMPLE,',
'',
'                ALTURA_U,',
'                POSICION, ',
'                SITIO_ID',
'                ',
'',
'',
'            ) VALUES (',
'                p.col001, --UNIDAD',
'                F_GET_LOVS(3, p.col002), --RACK',
'                p.col003, --u',
'                p.col004, --tipo RED',
'                (p.col005), --NOMBRE',
'                F_GET_LOVS(5, p.col006), --tIPO eQUIPO',
'                --LOWER(p.col007), --sub_tipo_equipo',
'',
'                (p.col007), --marca',
'                (p.col008), --modelo',
'                (p.col009),  --estado',
'                p.col010, --observaciones',
'                p.col011, --ni_ande',
'                p.col012, --serial_number',
'                p.col013, --version_hard',
'',
'                p.col014, --version_soft',
'                p.col015, --patch_ver',
'                p.col016, --ip_gestion',
'                p.col017, --ali1',
'                p.col018, --ali2',
'                p.col019, --pro_id',
'                parse_flexible_date(p.col020), --fecha_imple',
'',
'                (p.col021), -- altura',
'                (p.col022), --posicion,',
'                F_GET_LOVS(4, p.col002) --SITIO  ',
'            );',
'',
'            v_count := v_count + 1;',
'',
'        EXCEPTION',
'            ',
'            WHEN OTHERS THEN',
'                v_error_count := v_error_count + 1;',
'                apex_debug.error(''Mod: Equipos: Func DL: Error en fila: ''|| TO_CHAR(v_count + v_error_count) || '': '' || SQLERRM);',
'                CONTINUE;',
'        END;',
'',
'    END LOOP;',
'',
'    :P6_ERROR_ROW_COUNT := v_error_count;',
'    :P6_LOGGER := ''Registros insertados: '' || v_count || '', Errores: '' || v_error_count;',
'',
'    apex_debug.info(''Registros insertados: '' || v_count);',
'    apex_debug.info(''Errores: '' || v_error_count);',
'',
'   ',
'END;',
''))
