'        MAC_ADDRESS,',
'        HW_VERSION,',
'        SW_VERSION)',
'                values ',
'                (:P29_EQUIPO, ',
'                TabOut(indx).NOMBRE, ',
'                TabOut(indx).ETIQUETA, ',
'                TabOut(indx).ESTADO, ',
'                TabOut(indx).MODO, ',
'                TabOut(indx).DUPLEX, ',
