'*/',
'DECLARE',
'    l_seq    NUMBER := 10;',
'BEGIN',
'    -- Primero eliminar conexiones previas del enlace (si aplica)',
'    DELETE FROM sgt_conexiones_enlaces_wan',
'    WHERE enlace_wan_id = :P42_ENLACE_LOGICO_ID;',
