'        SELECT column_value AS tramo_id',
'        FROM TABLE(apex_string.split_numbers(:P42_TRAMO_ID, '':''))',
'        ORDER BY ROWNUM',
'    )',
'    LOOP',
'        INSERT INTO sgt_conexiones_enlaces_wan (',
'            enlace_wan_id,',
'            tramo_id,',
'            secuencia',
'        ) VALUES (',
'            :P42_ENLACE_LOGICO_ID;,',
'            rec.tramo_id,',
'            l_seq',
'        );',
'',
'        l_seq := l_seq + 10;',
'    END LOOP;',
'END;',
