,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'VALIDAR_POSICION_RACK'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'    v_equipo_id   NUMBER := TO_NUMBER(apex_application.g_x01);',
'    v_rack_id     NUMBER := TO_NUMBER(apex_application.g_x02);',
'    v_u_inicio    NUMBER := TO_NUMBER(apex_application.g_x03);',
'    v_altura_u    NUMBER := TO_NUMBER(apex_application.g_x04);',
'    v_posicion    VARCHAR2(10) := apex_application.g_x05;',
'',
'    v_total_u     NUMBER;',
'    v_u_fin       NUMBER;',
'    v_conflictos  NUMBER;',
'BEGIN',
'    v_u_fin := v_u_inicio + v_altura_u - 1;',
'',
unistr('    /* \D83D\DD12 Validar l\00EDmites del rack */'),
'    SELECT altura_u',
'    INTO v_total_u',
'    FROM sgt_racks',
'    WHERE id = v_rack_id;',
'',
'    apex_json.open_object;',
'',
'    IF v_u_inicio < 1 OR v_u_fin > v_total_u THEN',
'        apex_json.write(''disponible'', ''N'');',
'        apex_json.write(',
'            ''mensaje'',',
unistr('            ''El equipo excede el l\00EDmite del rack (1 - '' || v_total_u || '' U)'''),
'        );',
'        apex_json.close_object;',
'        RETURN;',
'    END IF;',
'',
unistr('    /* \D83D\DD0D Validar solapamiento (ignora el equipo editado) */'),
'    SELECT COUNT(*)',
'    INTO v_conflictos',
'    FROM sgt_equipos e',
'    WHERE e.rack_id = v_rack_id',
'      AND e.posicion = v_posicion',
'      AND (v_equipo_id IS NULL OR e.id <> v_equipo_id)',
'      AND (',
'            (v_u_inicio BETWEEN e.u AND e.u + e.altura_u - 1)',
'         OR (v_u_fin BETWEEN e.u AND e.u + e.altura_u - 1)',
'         OR (e.u BETWEEN v_u_inicio AND v_u_fin)',
'      );',
'',
'    IF v_conflictos > 0 THEN',
'        apex_json.write(''disponible'', ''N'');',
'        apex_json.write(',
'            ''mensaje'',',
unistr('            ''Existe un equipo ocupando ese rango de U en la posici\00F3n '' || v_posicion'),
'        );',
'    ELSE',
'        apex_json.write(''disponible'', ''S'');',
unistr('        apex_json.write(''mensaje'', ''La posici\00F3n est\00E1 disponible'');'),
'    END IF;',
'',
'    apex_json.close_object;',
'END;',
''))
,p_process_clob_language=>'PLSQL'
