'    END LOOP;',
'',
'    -- ===========================================',
'    -- 4. INSERTAR datos en CONEXIONES_ENLACE_WAN',
'    -- ===========================================',
'    l_seq := 10;',
'    FOR rec IN (',
'        SELECT column_value AS tramo_id',
'        FROM TABLE(apex_string.split_numbers(:P403_CONEXIONES, '',''))',
