'    FOR i IN 1 .. sitios_ids.COUNT LOOP ',
'        apex_debug.message(''IDS pasados: p_id=%s'', sitios_ids(i)); ',
'    END LOOP; ',
'    SELECT JSON_ARRAYAGG( ',
'             JSON_OBJECT(''id'' VALUE id, ''label'' VALUE label, ''shape'' VALUE ''box'', ''color'' VALUE ''#00AAFF'') ',
