'            AUDIT_DETAIL_ID,   ',
'            AUDIT_ID,  ',
'            TABLE_NAME,   ',
'            COLUMN_NAME,   ',
'            OLD_VALUE,   ',
'            NEW_VALUE,  ',
'            PK_COLUMN,   ',
'            PK_VALUE  ',
'        ) VALUES (  ',
'            AUDIT_DETAIL_SEQ.NEXTVAL,   ',
'            p_audit_id,  ',
'            p_table_name,  ',
'            p_column_name,   ',
'            p_old_value,   ',
'            p_new_value,  ',
'            p_record_key,   ',
'            p_key_value  ',
'        );  ',
'          ',
'    EXCEPTION  ',
'        WHEN OTHERS THEN  ',
'  ',
'            NULL;  ',
'    END LOG_CHANGES;  ',
'END AUDIT_PKG;',
'/ '))
);
