'        END IF;  ',
'  ',
'    ELSIF UPDATING THEN  ',
'  ',
'        IF NVL(:OLD.NOMBRE, ''NULL'') != NVL(:NEW.NOMBRE, ''NULL'') THEN  ',
'            AUDIT_PKG.LOG_CHANGES(v_audit_id, ''SGT_CIUDAD'', ''ID'', v_pk_value, ''NOMBRE'', :OLD.NOMBRE, :NEW.NOMBRE);  ',
